import { GoogleGenAI, Type } from "@google/genai";
import { GenerateCodeResponse, GeminiModelType, FileNode } from "../types";

const SYSTEM_INSTRUCTION = `
You are an expert full-stack web developer and UI/UX designer. 
Your task is to generate complete, functional, and aesthetically pleasing code for web applications based on user prompts.

Output Rules:
1. You MUST return a valid JSON object.
2. The JSON structure must strictly follow the schema provided.
3. You must generate ALL necessary files (HTML, CSS, JS, etc.) to make the application work.
4. If the user asks for a "dynamic" app, use vanilla JS or a simple lightweight framework approach that works in a browser environment without a build step if possible, or generate standard files that strictly import each other.
5. Do NOT provide explanation text outside the JSON.
6. Make the UI modern, colorful, and responsive using inline CSS or a dedicated style.css file.
7. If using images, use "https://picsum.photos/id/{id}/200/300" or similar placeholder services.

When modifying existing files, return the FULL content of the file, not just the diff.
`;

export const generateCode = async (
  prompt: string,
  modelId: GeminiModelType,
  currentFiles: FileNode[],
  apiKey: string
): Promise<GenerateCodeResponse> => {
  if (!apiKey) {
    throw new Error("API Key is missing.");
  }

  const ai = new GoogleGenAI({ apiKey });

  // Contextualize with current file structure if it exists
  const fileContext = currentFiles.map(f => `File: ${f.path}\nContent:\n${f.content}`).join("\n\n");
  const fullPrompt = `
    Current Project Files:
    ${fileContext}

    User Request: ${prompt}

    Generate or update the necessary files. 
  `;

  try {
    const response = await ai.models.generateContent({
      model: modelId,
      contents: fullPrompt,
      config: {
        systemInstruction: SYSTEM_INSTRUCTION,
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            message: {
              type: Type.STRING,
              description: "A short summary of what was built or changed."
            },
            files: {
              type: Type.ARRAY,
              items: {
                type: Type.OBJECT,
                properties: {
                  path: { type: Type.STRING, description: "Relative file path, e.g., 'index.html', 'js/app.js'" },
                  content: { type: Type.STRING, description: "Full code content of the file" }
                },
                required: ["path", "content"]
              }
            }
          },
          required: ["files", "message"]
        }
      }
    });

    const responseText = response.text;
    if (!responseText) throw new Error("No response from AI");

    return JSON.parse(responseText) as GenerateCodeResponse;

  } catch (error) {
    console.error("Gemini API Error:", error);
    throw error;
  }
};

import React from 'react';
