const getIcon = (filename: string) => {
  if (filename.endsWith('.html')) return <span className="text-orange-500 mr-2">&lt;/&gt;</span>;
  if (filename.endsWith('.css')) return <span className="text-blue-400 mr-2">#</span>;
  if (filename.endsWith('.js') || filename.endsWith('.ts') || filename.endsWith('.jsx') || filename.endsWith('.tsx')) return <span className="text-yellow-400 mr-2">JS</span>;
  if (filename.endsWith('.json')) return <span className="text-green-400 mr-2">{"{}"}</span>;
  return <span className="text-gray-400 mr-2">üìÑ</span>;
};

