import { AVAILABLE_MODELS } from '../constants';

interface ProjectWizardProps {
  messages: ChatMessage[];
  onSendMessage: (text: string) => void;
  isLoading: boolean;
  selectedModel: GeminiModelType;
  onModelChange: (model: GeminiModelType) => void;
  className?: string;
}

export const ProjectWizard: React.FC<ProjectWizardProps> = ({ 
  messages, 
  onSendMessage, 
  isLoading, 
  selectedModel,
  onModelChange,
  className 
}) => {
  const [input, setInput] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (input.trim() && !isLoading) {
      onSendMessage(input);
      setInput('');
    }
  };

  return (
    <div className={`flex flex-col h-full bg-white dark:bg-studio-bg ${className}`}>
      {/* Header */}
      <div className="p-4 border-b border-gray-200 dark:border-gray-700 flex justify-between items-center bg-gray-50 dark:bg-studio-panel">
        <h2 className="font-semibold text-gray-800 dark:text-white">AI Builder</h2>
