            {msg.relatedFiles && msg.relatedFiles.length > 0 && (
              <div className="mt-2 text-xs">
                <span className="text-gray-500 dark:text-gray-400 ml-2">Generated: </span>
                <div className="flex flex-wrap gap-2 mt-1">
                  {msg.relatedFiles.map(file => (
                    <span key={file} className="bg-gray-200 dark:bg-gray-700 text-gray-600 dark:text-gray-300 px-2 py-0.5 rounded border border-gray-300 dark:border-gray-600">
                      {file}
