import React, { useMemo, useState } from 'react';
import { FileNode } from '../types';

interface CodePreviewProps {
  files: FileNode[];
  selectedFile: string | null;
  onCodeChange: (content: string) => void;
  className?: string;
}

export const CodePreview: React.FC<CodePreviewProps> = ({ 
  files, 
  selectedFile, 
  onCodeChange,
  className 
}) => {
  const [activeTab, setActiveTab] = useState<'code' | 'preview'>('code');

  const file = files.find(f => f.path === selectedFile);

  // Generate the blob for the preview iframe
  const previewSrc = useMemo(() => {
    // Find entry point
    let entryHtml = files.find(f => f.path === 'index.html' || f.path.endsWith('.html'));

    // Fallback if no HTML file found but we have content
    if (!entryHtml && files.length > 0) {
      return '';
    }

    if (!entryHtml) return '';

    let content = entryHtml.content;

    // Very basic bundler: Replace script src and link href with inline blobs or content
    // This is a naive implementation for the "simulated" builder experience.

    // Inject CSS
    const cssFiles = files.filter(f => f.path.endsWith('.css'));
    cssFiles.forEach(css => {
       // Try to replace relative links
       // Case: <link rel="stylesheet" href="style.css">
       const regex = new RegExp(`<link[^>]+href=["']${css.path}["'][^>]*>`, 'g');
       content = content.replace(regex, `<style>\n${css.content}\n</style>`);

       // Case: link inside subfolder (not handled perfectly here, but good for flat structures)
    });

    // Inject JS
    const jsFiles = files.filter(f => f.path.endsWith('.js'));
    jsFiles.forEach(js => {
       // Case: <script src="app.js"></script>
       const regex = new RegExp(`<script[^>]+src=["']${js.path}["'][^>]*><\/script>`, 'g');
       content = content.replace(regex, `<script>\n${js.content}\n</script>`);
    });

    // Handle generic relative paths (simplified) - creating object URLs
    // Note: Creating object URLs for every render can be memory intensive, strictly for demo.
    // Ideally we would use a service worker or a proper bundler.

    return content;
  }, [files]);

  return (
    <div className={`flex flex-col h-full bg-gray-50 dark:bg-studio-bg ${className}`}>
      {/* Tabs */}
      <div className="flex border-b border-gray-200 dark:border-gray-700 bg-gray-100 dark:bg-studio-panel">
        <button
          onClick={() => setActiveTab('code')}
          className={`px-6 py-2 text-sm font-medium transition-colors ${
            activeTab === 'code' 
              ? 'bg-white dark:bg-studio-bg text-blue-600 dark:text-blue-400 border-t-2 border-blue-500' 
              : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200'
          }`}
        >
          Code Editor
        </button>
        <button
          onClick={() => setActiveTab('preview')}
          className={`px-6 py-2 text-sm font-medium transition-colors ${
            activeTab === 'preview' 
              ? 'bg-white dark:bg-studio-bg text-blue-600 dark:text-blue-400 border-t-2 border-blue-500' 
              : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-200'
          }`}
        >
          Live Preview
        </button>
      </div>

      <div className="flex-1 relative overflow-hidden">
        {/* Code Editor */}
        <div className={`absolute inset-0 ${activeTab === 'code' ? 'z-10' : 'z-0 invisible'}`}>
          {file ? (
            <textarea
              className="w-full h-full p-4 font-mono text-sm bg-white dark:bg-gray-900 text-gray-800 dark:text-gray-200 resize-none focus:outline-none"
              value={file.content}
              onChange={(e) => onCodeChange(e.target.value)}
              spellCheck={false}
              autoCapitalize="off"
            />
          ) : (
             <div className="flex items-center justify-center h-full text-gray-400 dark:text-gray-600">
               Select a file to edit
             </div>
          )}
        </div>

        {/* Preview */}
        <div className={`absolute inset-0 bg-white ${activeTab === 'preview' ? 'z-10' : 'z-0 invisible'}`}>
           {previewSrc ? (
             <iframe 
               title="preview"
               srcDoc={previewSrc}
               className="w-full h-full border-none"
               sandbox="allow-scripts allow-modals"
             />
           ) : (
             <div className="flex items-center justify-center h-full text-gray-400">
               No index.html found or empty project.
             </div>
           )}
        </div>
      </div>
    </div>
  );
};

