import React, { useState, useEffect } from 'react';
import { Sidebar } from './components/Sidebar';
import { ProjectWizard } from './components/ProjectWizard';
import { CodePreview } from './components/CodePreview';
import { Dashboard } from './components/Dashboard';
import { generateCode } from './services/geminiService';
import { FileNode, ChatMessage, GeminiModelType } from './types';
import { DEFAULT_FILES } from './constants';

const App: React.FC = () => {
  const [theme, setTheme] = useState<'light' | 'dark'>('dark');
  const [view, setView] = useState<'dashboard' | 'editor'>('dashboard');
  const [apiKey, setApiKey] = useState(process.env.API_KEY || '');

  // Project State
  const [files, setFiles] = useState<FileNode[]>([]);
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [selectedFile, setSelectedFile] = useState<string | null>(null);
  const [selectedModel, setSelectedModel] = useState<GeminiModelType>('gemini-3-pro-preview');
  const [isLoading, setIsLoading] = useState(false);

  // Initialize Theme
  useEffect(() => {
    if (theme === 'dark') {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [theme]);

  // Handlers
  const handleCreateProject = (initialPrompt?: string) => {
    setFiles(DEFAULT_FILES);
    setMessages([]);
    setSelectedFile('index.html');
    setView('editor');

    // Add initial system message
    setMessages([{
      id: 'system-1',
      role: 'system',
      text: 'Project initialized. Ask me to build something!',
      timestamp: Date.now()
    }]);

    if (initialPrompt) {
      handleSendMessage(initialPrompt);
    }
  };

  const handleSendMessage = async (text: string) => {
    // Add User Message
    const userMsg: ChatMessage = {
      id: Date.now().toString(),
      role: 'user',
      text: text,
      timestamp: Date.now()
    };
    setMessages(prev => [...prev, userMsg]);
    setIsLoading(true);

    try {
      // Call Gemini Service
      const response = await generateCode(text, selectedModel, files, apiKey);

      // Update Files
      const newFiles = [...files];
      const modifiedFiles: string[] = [];

      response.files.forEach(generatedFile => {
        const existingIndex = newFiles.findIndex(f => f.path === generatedFile.path);
        const fileObj: FileNode = {
          path: generatedFile.path,
          name: generatedFile.path.split('/').pop() || generatedFile.path,
          content: generatedFile.content,
          type: 'file',
          language: generatedFile.path.split('.').pop() || 'text'
        };

        if (existingIndex >= 0) {
          newFiles[existingIndex] = fileObj;
        } else {
          newFiles.push(fileObj);
        }
        modifiedFiles.push(generatedFile.path);
      });

      setFiles(newFiles);

      // If we modified index.html or currently selected file, ensure UI reflects it
      if (modifiedFiles.includes('index.html') && !selectedFile) {
        setSelectedFile('index.html');
      }

      // Add AI Response
      const aiMsg: ChatMessage = {
        id: (Date.now() + 1).toString(),
        role: 'model',
        text: response.message || "I've updated the code based on your request.",
        timestamp: Date.now(),
        relatedFiles: modifiedFiles
      };
      setMessages(prev => [...prev, aiMsg]);

    } catch (error) {
      console.error(error);
      const errorMsg: ChatMessage = {
        id: (Date.now() + 1).toString(),
        role: 'system',
        text: "Error generating code. Please check your API key or try a different prompt.",
        timestamp: Date.now()
      };
      setMessages(prev => [...prev, errorMsg]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCodeChange = (newContent: string) => {
    if (!selectedFile) return;
    setFiles(prev => prev.map(f => 
      f.path === selectedFile ? { ...f, content: newContent } : f
    ));
  };

  const handleCreateFile = () => {
    const name = prompt("Enter file name (e.g., style.css):");
    if (name) {
      const newFile: FileNode = {
        path: name,
        name: name,
        content: '',
        type: 'file',
        language: name.split('.').pop() || 'text'
      };
      setFiles([...files, newFile]);
      setSelectedFile(name);
    }
  };

  const handleDeleteFile = (path: string) => {
    if (confirm(`Are you sure you want to delete ${path}?`)) {
      setFiles(files.filter(f => f.path !== path));
      if (selectedFile === path) setSelectedFile(null);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-50 dark:bg-studio-bg text-gray-900 dark:text-gray-100 font-sans">
      {/* Navbar */}
      <header className="h-14 bg-white dark:bg-studio-panel border-b border-gray-200 dark:border-gray-700 flex items-center justify-between px-4 shrink-0">
        <div className="flex items-center space-x-2 cursor-pointer" onClick={() => setView('dashboard')}>
          <div className="w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center text-white font-bold text-lg">
            G
          </div>
          <span className="font-semibold text-lg tracking-tight">GenStudio</span>
        </div>

        <div className="flex items-center space-x-4">
           {view === 'editor' && (
             <button 
               onClick={() => handleSendMessage("Refactor the code to be cleaner and more optimized.")}
               className="text-xs bg-gray-100 dark:bg-gray-800 hover:bg-gray-200 dark:hover:bg-gray-700 px-3 py-1.5 rounded transition-colors"
               disabled={isLoading}
             >
               ‚ú® Refactor
             </button>
           )}
           <button 
             onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
             className="p-2 rounded-full hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
             title="Toggle Theme"
           >
             {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
           </button>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-hidden relative">
        {view === 'dashboard' ? (
          <Dashboard onCreateProject={handleCreateProject} />
        ) : (
          <div className="flex h-full">
            {/* Sidebar (Files) */}
            <div className="w-64 h-full shrink-0">
              <Sidebar 
                files={files}
                selectedFile={selectedFile}
                onSelectFile={setSelectedFile}
                onCreateFile={handleCreateFile}
                onDeleteFile={handleDeleteFile}
                className="h-full"
              />
            </div>

            {/* Center (Preview/Code) */}
            <div className="flex-1 h-full min-w-0 border-r border-gray-200 dark:border-gray-700">
               <CodePreview 
                 files={files}
                 selectedFile={selectedFile}
                 onCodeChange={handleCodeChange}
                 className="h-full"
               />
            </div>

            {/* Right Panel (Chat) */}
            <div className="w-80 lg:w-96 h-full shrink-0">
               <ProjectWizard 
                 messages={messages}
                 onSendMessage={handleSendMessage}
                 isLoading={isLoading}
                 selectedModel={selectedModel}
                 onModelChange={setSelectedModel}
                 className="h-full"
               />
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

