interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost' | 'subscribe';
  size?: 'sm' | 'md' | 'lg';
}

export const Button: React.FC<ButtonProps> = ({ 
  children, 
  variant = 'primary', 
  size = 'md', 
  className = '', 
  ...props 
}) => {
  const baseStyles = "inline-flex items-center justify-center rounded-full font-bold transition-all duration-300 transform active:scale-95 focus:outline-none disabled:opacity-50 disabled:cursor-not-allowed backdrop-blur-sm whitespace-nowrap";

  const variants = {
    primary: "bg-cta-gradient text-white shadow-lg hover:shadow-purple-500/30 hover:-translate-y-1",
    secondary: "bg-[#06B6D4] text-white shadow-lg hover:bg-[#0891b2]",
    outline: "border-2 border-[#06B6D4] text-[#06B6D4] hover:bg-[#06B6D4] hover:text-white",
    ghost: "text-slate-600 hover:text-[#06B6D4] dark:text-slate-300 dark:hover:text-[#06B6D4] bg-transparent",
    subscribe: "bg-white dark:bg-slate-800 text-[#06B6D4] border-2 border-[#06B6D4]"
  };

  const sizes = {
    sm: "px-3 md:px-4 py-1.5 md:py-2 text-xs md:text-sm",
    md: "px-5 md:px-6 py-2 md:py-3 text-sm md:text-base",
    lg: "px-6 md:px-8 py-3 md:py-4 text-base md:text-lg"
  };

  return (
    <button 
      className={`${baseStyles} ${variants[variant]} ${sizes[size]} ${className}`} 
      {...props}
    >
      {children}
    </button>
  );
};

export const BackToTop: React.FC = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const toggleVisibility = () => {
      setIsVisible(window.pageYOffset > 300);
    };
    window.addEventListener('scroll', toggleVisibility);
    return () => window.removeEventListener('scroll', toggleVisibility);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <button
      onClick={scrollToTop}
      className={`fixed bottom-6 md:bottom-8 right-6 md:right-8 z-[60] w-10 h-10 md:w-12 md:h-12 bg-white dark:bg-slate-800 text-[#06B6D4] rounded-full shadow-2xl flex items-center justify-center transition-all duration-500 border border-slate-100 dark:border-slate-700 ${
        isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10 pointer-events-none'
      }`}
    >
      <ChevronUp size={24} />
    </button>
  );
};

export const SideNavigation: React.FC<{ items: { id: string; label: string }[] }> = ({ items }) => {
  const [activeId, setActiveId] = useState<string>('');

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) setActiveId(entry.target.id);
        });
      },
      { threshold: 0.5, rootMargin: '-100px 0px -40% 0px' }
    );
    items.forEach((item) => {
      const el = document.getElementById(item.id);
      if (el) observer.observe(el);
    });
    return () => observer.disconnect();
  }, [items]);

  const scrollToSection = (id: string) => {
    const element = document.getElementById(id);
    if (element) {
      const yOffset = -80;
      const y = element.getBoundingClientRect().top + window.pageYOffset + yOffset;
      window.scrollTo({ top: y, behavior: 'smooth' });
    }
  };

  return (
    <nav className="fixed left-4 top-1/2 -translate-y-1/2 z-[40] hidden lg:flex flex-col gap-4 bg-white/40 dark:bg-slate-900/40 backdrop-blur-md p-3 rounded-2xl border border-white/20 dark:border-slate-800 shadow-xl">
      {items.map((item, idx) => (
        <button key={item.id} onClick={() => scrollToSection(item.id)} className="group relative flex items-center">
          <div className={`w-3 h-3 rounded-full transition-all duration-300 ${activeId === item.id ? 'bg-[#06B6D4] scale-125 ring-4 ring-[#06B6D4]/20' : 'bg-slate-300 dark:bg-slate-700'}`} />
          <span className="absolute left-8 px-2 py-1 rounded bg-slate-800 text-white text-[10px] font-bold opacity-0 group-hover:opacity-100 whitespace-nowrap">{item.label}</span>
        </button>
      ))}
    </nav>
  );
};

export const Typewriter: React.FC<{ words: string[]; speed?: number; delay?: number }> = ({ words, speed = 150, delay = 2000 }) => {
  const [index, setIndex] = useState(0);
  const [subIndex, setSubIndex] = useState(0);
  const [reverse, setReverse] = useState(false);

  useEffect(() => {
    if (subIndex === words[index].length + 1 && !reverse) {
      const timeout = setTimeout(() => setReverse(true), delay);
      return () => clearTimeout(timeout);
    }
    if (subIndex === 0 && reverse) {
      setReverse(false);
      setIndex((prev) => (prev + 1) % words.length);
      return;
    }
    const timeout = setTimeout(() => setSubIndex((prev) => prev + (reverse ? -1 : 1)), reverse ? speed / 2 : speed);
    return () => clearTimeout(timeout);
  }, [subIndex, index, reverse, words, speed, delay]);

  return (
    <span className="inline-flex items-center text-transparent bg-clip-text bg-gradient-to-r from-purple-600 to-cyan-500 dark:from-purple-400 dark:to-cyan-400">
      {words[index]?.substring(0, subIndex)}
      <span className="ml-1 w-1 h-[1em] bg-cyan-500 animate-pulse"></span>
    </span>
  );
};

