const MagneticLogo = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [imgError, setImgError] = useState(false);
  const ref = useRef<HTMLAnchorElement>(null);

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!ref.current || window.innerWidth < 1024) return;
    const { clientX, clientY } = e;
    const { left, top, width, height } = ref.current.getBoundingClientRect();
    const x = clientX - (left + width / 2);
    const y = clientY - (top + height / 2);
    setPosition({ x: x * 0.1, y: y * 0.1 });
  };

  const handleMouseLeave = () => {
    setPosition({ x: 0, y: 0 });
  };

  return (
    <NavLink 
      to="/" 
      ref={ref}
      className="flex items-center gap-2 sm:gap-3 group relative z-50 shrink-0 py-1 rounded-2xl"
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
      style={{ transform: `translate(${position.x}px, ${position.y}px)`, transition: 'transform 0.2s cubic-bezier(0.33, 1, 0.68, 1)' }}
    >
      <div className="absolute inset-0 bg-white/40 dark:bg-slate-900/40 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-700 -z-10 blur-lg scale-110" />

