  const location = useLocation();

  useEffect(() => {
    setActiveDropdown(null);
    setIsMobileMenuOpen(false);
  }, [location.pathname]);

  const toggleTheme = () => {
    const newTheme = !isDark;
    setIsDark(newTheme);
    if (newTheme) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  };

  const handleDropdownToggle = (label: string, e: React.MouseEvent) => {
    e.preventDefault(); 
    setActiveDropdown(activeDropdown === label ? null : label);
  };

  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-white/90 dark:bg-[#020617]/95 backdrop-blur-2xl border-b border-slate-100 dark:border-slate-800 transition-all duration-300 shadow-sm">
