import React, { useState, useEffect } from 'react';
import { Smartphone, Monitor, RotateCcw, Maximize, Minimize } from 'lucide-react';

export const PreviewMode: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [isMobile, setIsMobile] = useState(false);
  const [key, setKey] = useState(0);
  const [isFullscreen, setIsFullscreen] = useState(false);

  useEffect(() => {
    const handleFsChange = () => {
      setIsFullscreen(!!document.fullscreenElement);
    };
    document.addEventListener('fullscreenchange', handleFsChange);
    return () => document.removeEventListener('fullscreenchange', handleFsChange);
  }, []);

  // Lock body scroll when in mobile preview to avoid double scrollbars
  useEffect(() => {
    if (isMobile) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }
    return () => { document.body.style.overflow = ''; };
  }, [isMobile]);

  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen().catch((e) => {
        console.error("Failed to enter fullscreen:", e);
      });
    } else {
      document.exitFullscreen().catch((e) => {
        console.error("Failed to exit fullscreen:", e);
      });
    }
  };

  const reload = () => {
    setKey(prev => prev + 1);
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 font-sans ${isMobile ? 'bg-slate-200 dark:bg-[#0f172a] flex items-center justify-center h-screen w-full' : 'bg-transparent'}`}>

      {/* Floating Toolbar */}
      <div className={`fixed z-[9999] top-6 left-1/2 -translate-x-1/2 flex items-center gap-3 p-2 bg-white/90 dark:bg-slate-900/90 backdrop-blur-md border border-slate-200 dark:border-slate-700 rounded-full shadow-2xl transition-all duration-300 hover:opacity-100 ${isFullscreen && !isMobile ? 'opacity-0 -translate-y-[200%]' : 'opacity-100'}`}>

        {/* Device Toggles */}
        <div className="flex bg-slate-100 dark:bg-slate-800 rounded-full p-1">
          <button 
            onClick={() => setIsMobile(false)}
            className={`p-2.5 rounded-full transition-all duration-300 flex items-center justify-center ${!isMobile ? 'bg-white dark:bg-slate-700 text-[#06B6D4] shadow-sm' : 'text-slate-500 hover:text-slate-700 dark:text-slate-400 hover:bg-slate-200 dark:hover:bg-slate-700/50'}`}
            title="Desktop View"
          >
            <Monitor size={20} />
          </button>
          <button 
            onClick={() => setIsMobile(true)}
            className={`p-2.5 rounded-full transition-all duration-300 flex items-center justify-center ${isMobile ? 'bg-white dark:bg-slate-700 text-[#06B6D4] shadow-sm' : 'text-slate-500 hover:text-slate-700 dark:text-slate-400 hover:bg-slate-200 dark:hover:bg-slate-700/50'}`}
            title="Mobile View"
          >
            <Smartphone size={20} />
          </button>
        </div>

        <div className="w-px h-8 bg-slate-300 dark:bg-slate-700"></div>

        {/* Actions */}
        <div className="flex gap-1">
          <button 
            onClick={reload}
            className="p-3 rounded-full hover:bg-slate-100 dark:hover:bg-slate-800 text-slate-600 dark:text-slate-300 transition-colors flex items-center justify-center"
            title="Reload Preview"
          >
            <RotateCcw size={20} />
          </button>

          <button 
            onClick={toggleFullscreen}
            className="p-3 rounded-full hover:bg-slate-100 dark:hover:bg-slate-800 text-slate-600 dark:text-slate-300 transition-colors flex items-center justify-center"
            title={isFullscreen ? "Exit Full Screen" : "Full Screen"}
          >
            {isFullscreen ? <Minimize size={20} /> : <Maximize size={20} />}
          </button>
        </div>
      </div>

      {/* Content Area */}
      {isMobile ? (
        <div className="relative animate-in zoom-in-95 duration-500">
           {/* Phone Mockup */}
           <div className="w-[390px] h-[844px] max-h-[90vh] bg-white dark:bg-black rounded-[3.5rem] border-[12px] border-slate-900 shadow-2xl overflow-hidden relative ring-1 ring-slate-900/5">
              {/* Dynamic Island / Notch */}
              <div className="absolute top-0 left-0 right-0 h-8 bg-slate-900 z-50 flex justify-center pointer-events-none">
                 <div className="w-32 h-6 bg-black rounded-b-3xl"></div>
              </div>

              {/* App Container - Transform creates a new stacking context for fixed elements */}
              <div className="w-full h-full overflow-y-auto overflow-x-hidden scrollbar-hide bg-white dark:bg-[#020617] scroll-smooth">
                 <div key={key} className="w-full min-h-full transform translate-z-0">
                   {children}
                 </div>
              </div>
           </div>

           {/* Device Label */}
           <div className="absolute -bottom-10 left-0 right-0 text-center text-slate-400 text-sm font-medium">
             iPhone 14 Pro â Mobile View
           </div>
        </div>
      ) : (
        /* Desktop View - Native Scrolling */
        <div key={key} className="w-full min-h-screen">
          {children}
        </div>
      )}

    </div>
  );
};

