                      }
                    >
                      {item.label}
                    </NavLink>
                    {item.children && (
                      <button onClick={(e) => { e.preventDefault(); handleDropdownToggle(item.label, e); }} className="p-3 text-slate-500">
                         <ChevronDown size={20} className={`transition-transform ${activeDropdown === item.label ? 'rotate-180' : ''}`} />
